class Solution 
{
    int f(int a_idx, int sum, int q_idx, vector<vector<int>>& queries) 
    {
        if(sum == 0) return q_idx;
        if(q_idx == queries.size()) return 1e5;

        int &ans = dp[sum][q_idx]; if(ans != -1) return ans;

        ans = f(a_idx, sum, q_idx + 1, queries);

        if(queries[q_idx][0] <= a_idx and queries[q_idx][1] >=  a_idx and queries[q_idx][2] <= sum)
        {
            ans = min(ans, f(a_idx, sum - queries[q_idx][2], q_idx + 1, queries));
        }

        return ans;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), ans = 0;
        for(int i = 0; i < n; ++i)
        {
            for(int j = 0; j <= nums[i]; ++j)
            {
                for(int k = 0; k <= queries.size(); ++k)
                {
                    dp[j][k] = -1;
                }
            }

            ans = max(ans, f(i, nums[i], 0, queries));
        }
        return ans > queries.size() ? -1 : ans;
    }
};

