using namespace std;
class Solution {
public:
    int minZeroArray(vector<int>& n,vector<vector<int>>& q){
        int s=n.size();
        vector<int> m(s,-1);
        for(int j=0;j<s;++j){
            int t=n[j];
            if(t==0)
            {
                m[j]=-1;
                continue;
            }
            vector<int> r;
            for(int i=0;i<q.size();++i){
                int l=q[i][0],r1=q[i][1];
                if(l<=j&&j<=r1)r.push_back(i);
            }
            unordered_set<int> p;
            p.insert(0);
            bool f=false;
            for(int i:r){
                int v=q[i][2];
                unordered_set<int> n_p=p;
                bool b=false;
                for(int s1:p){
                    int n_s=s1+v;
                    if(n_s==t){
                        m[j]=i;
                        f=true;
                        b=true;
                        break;
                    }
                    else if(n_s<t)n_p.insert(n_s);
                }
                if(b)break;
                p=n_p;
            }
            if(!f){return -1;}
        }
        int mx=-1;
        for(int j=0;j<s;++j){
            if(n[j]!=0)mx=max(mx,m[j]);
        }
        if(mx==-1){
            return 0;
        }
        else{
            return mx+1;
        }
    }
};

