class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& queries) {
        int n = a.size();
        int m = queries.size();

        auto is = [&](array<int, 11> fr, int val) {
            vector<int> dp(val + 1, 0);
            dp[0] = 1;
            // cout << "val:: " << val << endl;
            for(int j = 1; j <= 10; j++) {
                if(!fr[j]) continue;
                // cout << "j:: " << j << " fr:: " << fr[j] << endl;

                    for(int i = val; i >= 0; i--) {
                        for(int k = 1; k <= fr[j]; k++) {
                        if(dp[i]) {
                            if(i + j * k <= val) {
                                // cout << "i:: " << i <<  " i + j *k:: "  << i + j*k << endl;
                                dp[i + j * k] = 1;
                            }
                        }
                    }
                }
            }
            return dp[val];
        };

        auto okk = [&](int len) {
            // cout << "len:: " << len << endl;
            vector<array<int, 11>> pref(n);
            for(int i = 0; i < len; i++) {
                int l = queries[i][0];
                int r = queries[i][1];
                int val = queries[i][2];
                pref[l][val] += 1;
                if(r + 1 < n) pref[r + 1][val] -= 1;
            }
            for(int i = 1; i < n; i++) {
                for(int j = 0; j < 11; j++) {
                    pref[i][j] += pref[i - 1][j];
                }
            }
            for(int i = 0; i < n; i++) {
                array<int, 11> fr = pref[i];
                if(!is(fr, a[i])) return false;
            }
            return true;
        };



        int l = 0, r = m, res = -1;
        while(l <= r) {
            int mid = (l + r) / 2;
            if(okk(mid)) {
                res = mid;
                r = mid - 1;
            }else {
                l = mid + 1;
            }
        }
        return res;
    }
};

