class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = sum(num > 0 for num in nums)
        if m == 0: return 0
        ans = [-1] * n
        for i, num in enumerate(nums):
            if num == 0: 
                ans[i] = 0
                continue
            seen = set([num])
            for k, (l, r, val) in enumerate(queries):
                if not l <= i <= r and num >= val: continue
                if val in seen:
                    ans[i] = k + 1
                    break
                for elem in seen.copy():
                    if elem - val > 0:
                        seen.add(elem - val)
        return -1 if -1 in ans else max(ans)

