class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        vector<int> minRequired(n, 1e9); // Stores the minimum query index required to make nums[i] zero.

        // Count initial zeros
        int zeroCount = count(nums.begin(), nums.end(), 0);
        if (zeroCount == n) return 0; // If all elements are already zero, return 0.

        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) {
                minRequired[i] = 0;
                continue;
            }

            vector<pair<int, int>> choice; // Stores {queryIndex, x}
            for (int j = 0; j < q; j++) {
                int l = queries[j][0], r = queries[j][1], x = queries[j][2];
                if (l <= i && i <= r) {
                    choice.push_back({j + 1, x}); // Store (1-based index, value)
                }
            }

            int c = choice.size();
            if (c == 0) continue; // If no query affects nums[i], skip.

            vector<vector<int>> nxtIndex(c + 1, vector<int>(1001, 1e9));

            for (int level = c - 1; level >= 0; level--) {
                for (int value = 0; value <= 1000; value++) {
                    nxtIndex[level][value] = 1e9;
                    int x = choice[level].second;

                    if (value == 0 || value == x) {
                        nxtIndex[level][value] = level;
                        continue;
                    }

                    // Carry forward the previous best index
                    if (level + 1 < c) {
                        nxtIndex[level][value] = nxtIndex[level + 1][value];
                    }

                    // Reduce value using x
                    if (level + 1 < c && value - x >= 0) {
                        nxtIndex[level][value] = min(nxtIndex[level][value], nxtIndex[level + 1][value - x]);
                    }
                }
            }

            int finalChoice = nxtIndex[0][nums[i]];
            if (finalChoice != 1e9) {
                minRequired[i] = choice[finalChoice].first;
            }
        }

        int ans = *max_element(minRequired.begin(), minRequired.end());
        return (ans == 1e9) ? -1 : ans;
    }
};

