class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, m = len(nums), len(queries)
        idx_coins = [[] for _ in range(n)]
        for j, (l, r, v) in enumerate(queries):
            for i in range(l, r + 1):
                idx_coins[i].append((j, v))
        for i in range(n):
            idx_coins[i].sort(key=lambda x: x[1])
        def can_sum(i, k):
            target = nums[i]
            dp = [True] + [False] * target
            for j, coin in idx_coins[i]:
                if j < k:
                    for s in range(target, coin - 1, -1):
                        if dp[s - coin]:
                            dp[s] = True
            return dp[target]
        thresholds = [0] * n
        for i in range(n):
            if nums[i] == 0:
                continue
            lo, hi, found = 0, m + 1, None
            while lo < hi:
                mid = (lo + hi) // 2
                if can_sum(i, mid):
                    found = mid
                    hi = mid
                else:
                    lo = mid + 1
            if found is None:
                return -1
            thresholds[i] = found
        return max(thresholds)

