class Solution {
public:
    int dp[1001][1001];

    int solve(int i, int target, vector<int>& a, int n) {
        if (target == 0) return i;
        if (i == n) return 1e9;
        if (dp[i][target] != -1) return dp[i][target];

        int notTake = solve(i + 1, target, a, n);
        int take = 1e9;
        if (target >= a[i]) {
            take = solve(i + 1, target - a[i], a, n);
        }

        return dp[i][target] = min(notTake, take);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();
        vector<int> miniQ;

        for (int i = 0; i < n; i++) {
            vector<int> temp, tt;
            for (int j = 0; j < q; j++) {
                if (i >= queries[j][0] && i <= queries[j][1]) {
                    temp.push_back(queries[j][2]);
                    tt.push_back(j);
                }
            }

            memset(dp, -1, sizeof(dp));
            int ans = solve(0, nums[i], temp, temp.size());
            if (ans >= 1e9) return -1;
            if(ans == 0){
                miniQ.push_back(0);
            }else{
                miniQ.push_back(tt[ans-1]+1);
            }    
        }

        return *max_element(miniQ.begin(), miniQ.end());
    }
};

