class Solution {
    public int minZeroArray(int[] nums, int[][] que) {
        int n = nums.length;
        int m = que.length;

        List<BitSet> dp = new ArrayList<>();
        for (int j = 0; j < n; j++) {
            BitSet bs = new BitSet(1001);
            bs.set(0);
            dp.add(bs);
        }

        boolean fl = true;
        for (int num : nums) {
            if (num != 0) {
                fl = false;
                break;
            }
        }

        if (fl) return 0;

        for (int k = 0; k < m; k++) {
            int l = que[k][0];
            int r = que[k][1];
            int v = que[k][2];

            for (int j = l; j <= r; j++) {
                BitSet bit = new BitSet(1001);
                BitSet curr = dp.get(j);
                for (int pos = curr.nextSetBit(0); pos >= 0; pos = curr.nextSetBit(pos + 1)) {
                    if (pos + v < 1001) {
                        bit.set(pos + v);
                    }
                }
                curr.or(bit);

                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    curr.clear(pos);
                }
            }

            boolean flag = true;
            for (int j = 0; j < n; j++) {
                if (!dp.get(j).get(nums[j])) {
                    flag = false;
                    break;
                }
            }

            if (flag) return k + 1;
        }

        return -1;
    }
}

