class Solution {
public:
    bool possible (vector<int>& nums, vector<vector<int>>& q, int k){
        int n = nums.size();
        vector<int> diff (n+1, 0);
        for (int i = 0; i < k; i++) {
            int start = q[i][0], end = q[i][1], val = q[i][2];
            diff[start] += val;
            diff[end + 1] -= val;
        }
        vector <int> app (n, 0);
        int sum = 0;
        for (int i = 0; i < n; i++) {
            sum += diff[i];
            app[i] = sum;
        }
        for (int i = 0; i < n; i++) {
            if (app[i] < nums[i]) return false; 
            int extra = app[i] - nums[i];
            if (extra > 0) { 
                vector <bool> dp (nums[i] + 1, false);
                dp[0] = true; 
                for (int j = 0; j < k; j++) {
                    if (q[j][0] <= i && q[j][1] >= i) {
                        int val = q[j][2];
                        for (int k = nums[i]; k >= val; k--) {
                            dp[k] = dp[k] || dp[k - val];
                        }
                    }
                }
                if (!dp[nums[i]]) return false; 
            }
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size(), l = 0, r = q.size(); 
        if (!possible (nums, q, r)) return -1; 
        while (l <= r){
            int mid = l + (r - l) / 2; 
            if (possible (nums, q, mid)) r = mid - 1; 
            else l = mid + 1; 
        }
        return l; 
    }
};

