                for (int j = 0; j <= 1000 - v; ++j)
                    if (dp[i][j + v]) dp[i][j] = 1;
            bool found = true;
            for (int i = 0; i < n; ++i)
                if (!dp[i][0]) found = false;
            if (found) return k + 1;
        }
        return -1;
    }
};

