class Solution:
    def initializeBitsets(self, nums):
        bitsets = [set() for _ in nums]
        for bitset in bitsets:
            bitset.add(0)
        return bitsets

    def checkAllZeros(self, nums):
        return all(num == 0 for num in nums)

    def applyQuery(self, bitsets, left, right, increment, nums):
        for index in range(left, right + 1):
            updatedSet = {value + increment for value in bitsets[index]}
            bitsets[index].update(updatedSet)
            bitsets[index] = {value for value in bitsets[index] if value <= 1000}

    def checkAllAchieved(self, bitsets, nums):
        return all(num in bitset for bitset, num in zip(bitsets, nums))

    def minZeroArray(self, nums, queries):
        if self.checkAllZeros(nums):
            return 0

        bitsets = self.initializeBitsets(nums)

        for queryIndex, (left, right, increment) in enumerate(queries):
            self.applyQuery(bitsets, left, right, increment, nums)

            if self.checkAllAchieved(bitsets, nums):
                return queryIndex + 1

        return -1

