class Solution {
int f(int ind, vector<vector<int>> &queries, int sum, int i, vector<int> &nums, vector<unordered_map<int, int>> &dp)
{
    if(sum == 0) return ind;
    if(ind == queries.size()) return INT_MAX;
    if(dp[ind].find(sum) != dp[ind].end()) return dp[ind][sum];
    int ans = f(ind + 1, queries, sum, i, nums, dp);
    if(sum >= queries[ind][2] && queries[ind][0] <= i && i <= queries[ind][1])
    {
        ans = min(ans, f(ind + 1, queries, sum - queries[ind][2], i, nums, dp));
    }
    return dp[ind][sum] = ans;
}
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int ans = -1;

        for(int i = 0; i < nums.size(); i++)
            {
                vector<unordered_map<int, int>> dp(queries.size() + 1);
                int x  =f(0, queries, nums[i], i, nums, dp);
                if(x == INT_MAX) return -1;
                ans = max(ans, x);
            }
        return ans;
    }
};

