public:

    void ssum(vector<int>&v, int s) {
        int n=v.size();
        //memset(dp,0,sizeof(dp));
        for(int i=0;i<=n;i++) {
            dp[i][0]=1;
        }
        for(int i=1;i<=n;i++) {
            for(int j=1;j<=s;j++) {
                if(v[i-1]>j) {
                    dp[i][j]=dp[i-1][j];
                } else {
                    dp[i][j]=dp[i-1][j] || dp[i-1][j-v[i-1]];
                }
            }
        }
    }



    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        int j=0, c0=0;
        for(int i=0;i<n;i++) v[i].clear();
        for(vector<int>p: queries) {
            int l=p[0], r=p[1], val=p[2];
            for(int i=l;i<=r;i++) v[i].push_back({val, j});
            j++;
        }
        for(int i: nums) {
            c0+=(i==0);
        }

        if(c0==n) return 0;
        int re=-1;


        for(int i=0;i<n;i++) {
           int lo=0, hi=v[i].size()-1, ans=-1;
            while(lo<=hi) {
                int mi=(lo+hi)/2;
                vector<int>tmp;
                for(int j=0;j<=mi;j++) {
                    tmp.push_back(v[i][j].first);
                }
                ssum(tmp, nums[i]);
                if(dp[tmp.size()][nums[i]]) ans=v[i][mi].second, hi=mi-1;
                else lo=mi+1;
            }
            if(ans==-1) return -1;
            re=max(re, ans+1);
        }
        return re;




    }
};

