class Solution {
    vector<int> count;
    int total(){
        int sum = 0;
        for(int i=1; i<=10; ++i)
            sum += i*count[i];
        return sum;
    }
    bool canCreate(int val){ // construct val by count
        int sum = total();
        if(sum < val) return false;
        else if(sum == val) return true;
        // use DP
        vector<bool> dp(val + 1, false);
        dp[0] = true; 
        for(int i = 1; i <= 10; ++i) {
            int num = i, max_count = count[i];
            vector<bool> new_dp = dp;
            for (int remainder = 0; remainder < num; remainder++) {
                deque<int> dq;
                for (int j = remainder; j <= val; j += num) {
                    if (!dq.empty() && j - dq.front() > max_count * num)
                        dq.pop_front();

                    if (dp[j])
                        dq.push_back(j);

                    if (!dq.empty())
                        new_dp[j] = true;
                }
            }
            dp = new_dp;
        }
        return dp[val];
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        vector<vector<int>> minus(11);
        count.resize(11,0); // 1~10
        int indQ = 0;
        for(int i=0; i<nums.size(); ++i){
            for(auto i : minus[i]){
                if(i < 0)
                    count[-i]--;
                else
                    count[i]++;
            }
            int val = nums[i];
            if(val == 0) continue;
            // check if cur can form val
            while(indQ < queries.size() && canCreate(val) == false){
                // add queries[indQ]
                if(queries[indQ][1] >= i){
                    minus[queries[indQ][1]+1].push_back(-1*queries[indQ][2]);
                    if(queries[indQ][0] <= i) count[queries[indQ][2]]++;
                    else minus[queries[indQ][0]].push_back(queries[indQ][2]);

                }
                ++indQ;
            }
            if(canCreate(val) == false) return -1; // all use, still cannot
        }
        return indQ;
    }
};

