class Solution {
private:
    bool check(int mid, vector<int>& nums, vector<vector<int>>& q) {

        int n = nums.size();
        vector<vector<int>> key(n,vector<int>());

        for (int i = 0; i < mid; i++) {

            for (int k = q[i][0]; k <= q[i][1]; k++) {
                key[k].push_back(q[i][2]);
            }
        }

        for (int i = 0; i < n; i++) {

            if (nums[i] == 0) continue;

            if (!isValid(nums[i],key[i])) {
                return false;
            }
        }

        return true;
    }

    bool isValid(int target, vector<int>& nums) {

        int n = nums.size();
        vector<bool> key(target+1,false); key[0] = true;

        for (auto num : nums) {

            for (int i = target-num; i >= 0; i--) {

                if (key[i]) key[i+num] = true;
            }
        }

        return key[target];
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int ans = -1;
        int low = 0; int high = queries.size();

        while (low <= high) {

            int mid = low + (high-low)/2;

            if (check(mid,nums,queries)) {
                ans = mid; high = mid-1;
            } else {
                low = mid+1;
            }
        }

        return ans;
    }
};

