class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        List<Set<Integer>> res = new ArrayList<>();
        for (int i = 0; i < nums.length; i++) {
            res.add(new HashSet<>());
            res.get(i).add(0);
        }
        boolean[] done = new boolean[nums.length];
        int ct = 0;
        for (int i = 0; i < nums.length; i++) {
            if (nums[i] == 0) {
                done[i] = true;
                ct++;
            }
        }
        if (ct == nums.length) return 0;

        for (int i = 0; i < queries.length; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for (int j = l; j <= r; j++) {
                if (done[j]) continue;
                Set<Integer> set = new HashSet<>();
                for (Integer v : res.get(j)) {
                    if (v + val == nums[j]) {
                        done[j] = true;
                        ct++;
                        break;
                    }
                    set.add(v+val);
                }
                if (!done[j]) {
                    res.get(j).addAll(set);
                }
            }
            if (ct == nums.length) return i + 1;
        }
        return -1;
    }
}

