using namespace std;

class Solution {
public:
    // find the min index to make target 0
    vector<pair<int, int>> arr;// val, index
    int target;
    int dp[1001][1001];
    int n;
    int ans = INT_MAX / 2;

    // sum in target, ind in arr
    int rec(int sum, int ind) {  // return min index
        // if(ind >= arr.size()) {
        //     if(sum == 0) {
        //         return ;
        //     } else {
        //         return INT_MAX / 2;
        //     }
        // }
        // auto [val, qidx] = arr[ind];
        // if(sum == 0) {
        //     return 
        // }
        if(sum < 0 || (ind >= arr.size() && sum != 0)) {
            return INT_MAX / 2;
        }
        int &ret = dp[sum][ind];
        if(ret != -1) return ret;
        // not found

        // not take
        ret = rec(sum, ind + 1);
        // take
        auto [val, qidx] = arr[ind];
        if(sum - val > 0) {
            ret = min(ret, rec(sum - val, ind + 1));
        } else if(sum - val == 0) {
            ans = min(ans, qidx);
            return ret = qidx;
        }
        return ret;
    }


    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {  // needs to be exactly
        n = nums.size();
        vector<vector<pair<int, int>>> decs(n);  // val, index
        for(int i = 0; i < q.size(); ++i) {
            int l = q[i][0], r = q[i][1], val = q[i][2];
            for(int j = l; j <= r; ++j) {
                decs[j].push_back({val, i});
            }
        }

        int ret = -1;
        for(int i = 0; i < n; ++i) {
            arr = decs[i];
            target = nums[i];
            memset(dp, -1, sizeof dp);
            ans = INT_MAX / 2;
            if(target == 0) ans = -1;
            else rec(target, 0);
            // if(ans != INT_MAX / 2) {
                // ret = min(ret, i - ans + 1);
            ret = max(ret, ans);

            // cout << ans << endl;
            // }
        }

        return ret >= INT_MAX / 2 ? -1 : (ret + 1);
    }
};

