#include <vector>
using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();

        vector<vector<bool>> store(n, vector<bool>(1001, false));
        for (int j = 0; j < n; j++) {
            store[j][0] = true;
        }

        int check1 = 1;
        for (int j = 0; j < n; j++) {
            if (nums[j] != 0) {
                check1 = 0;
                break;
            }
        }

        if (check1) return 0;

        for (int k = 0; k < queries.size(); k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            for (int j = l; j <= r; j++) {
                vector<bool> temp = store[j];  
                for (int pos = 1000 - v; pos >= 0; pos--) {
                    if (store[j][pos]) {
                        temp[pos + v] = true;
                    }
                }
                store[j] = temp;

                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    store[j][pos] = false;
                }
            }

            int check2 = 1;
            for (int j = 0; j < n; j++) {
                if (!store[j][nums[j]]) {
                    check2 = 0;
                    break;
                }
            }

            if (check2) return k + 1;
        }

        return -1;
    }
};

