class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        if all(x == 0 for x in nums):
            return 0

        sums = [0] * n
        dp = []

        for i in range(n):
            target = nums[i]
            newdp = [False] * (target + 1)
            newdp[0] = True
            dp.append(newdp.copy())

        for j in range(len(queries)):
            l, r, val = queries[j]
            for i in range(l, r + 1):
                if nums[i] == 0:
                    continue

                sums[i] += val
                new_t = nums[i]
                new_dp = [False] * (new_t + 1)
                for s in range(new_t + 1):
                    if dp[i][s]:
                        new_dp[s] = True
                        if s + val <= new_t:
                            new_dp[s + val] = True
                dp[i] = new_dp

            total = True
            for i in range(n):
                if nums[i] == 0:
                    continue
                if sums[i] < nums[i]:
                    total = False
                    break
                if not dp[i][nums[i]]:
                    total = False
                    break
            if total:
                return j + 1

        return -1

