class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int sum = accumulate(nums.begin(), nums.end(), 0);
        if (!sum) return 0;
        const int n = nums.size();
        const int m = queries.size();
        vector<unordered_set<int>> potentials(n, {0});

        vector<bool> done(n, false);
        int all_done = 0;
        for (int i = 0; i < m; ++i) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for (int j = l; j <= r; ++j) {
                if (done[j]) continue;
                auto& p = potentials[j];
                if (p.size() == 1) {
                    p.insert(val);
                } else {
                    unordered_set<int> tmp = p;
                    for (const int k : tmp) {
                        if (k + val > 1000 || p.count(k + val)) continue;
                        p.insert(k + val);
                    }
                }
                if (p.count(nums[j])) {
                    done[j] = true;
                    all_done++;
                }
            }
            if (all_done == n)
                return i+1;
        }
        return -1;
    }
};

