class Solution {
public:
    bool possible(multiset<int>& st, int target) {
        vector<int> temp;
        for(auto i : st) temp.push_back(i);

        const int n = temp.size();

        vector<vector<bool>> dp(target + 1, vector<bool>(n + 1, false));
        for(int i = 0; i <= n; i++) {
            dp[target][i] = true;
        }

        for(int i = target - 1; i >= 0; i--) {
            for(int j = n - 1; j >= 0; j--) {
                if(i + temp[j] <= target) {
                    dp[i][j] = dp[i][j] or dp[i + temp[j]][j + 1];
                }
                dp[i][j] = dp[i][j] or dp[i][j + 1];
            }
        }

        return dp[0][0];
    }
    bool valid(vector<int>& nums, vector<vector<int>>& queries, int mid) {
        const int n =  nums.size(), m = queries.size();
        vector<vector<int>> add(n + 1);
        vector<vector<int>> rem(n + 1);

        for(int i = 0; i < mid; i++) {
            int u = queries[i][0], v = queries[i][1], val = queries[i][2];
            add[u].push_back(val);
            rem[v + 1].push_back(val); 
        }

        multiset<int> st;

        for(int i = 0; i < n; i++) {
            if(!add[i].empty()) {
                for(auto j : add[i]) st.insert(j);
            }
            if(!rem[i].empty()){
                for(auto j : rem[i]) st.erase(st.find(j));
            }
            if(!possible(st, nums[i])) return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int lo = 0, hi = queries.size();
        int ans = -1;

        while(lo <= hi) {
            int mid = (lo + hi)/2;

            if(valid(nums, queries, mid)) {
                ans = mid;
                hi = mid - 1;
            }
            else {
                lo = mid + 1;
            }
        }

        return ans;
    }
};

