impl Solution {
    pub fn min_zero_array(nums: Vec<i32>, queries: Vec<Vec<i32>>) -> i32 {
        let mut dp: Vec<Vec<bool>> = Vec::with_capacity(10);
        for i in 0..nums.len() {
            let mut sub_dp = vec![false; nums[i] as usize + 1];
            sub_dp[0] = true;
            dp.push(sub_dp);
        }

        let mut zeros = nums.iter().filter(|&&v| v == 0).count();
        let mut k = 0;
        while zeros < nums.len() && k < queries.len() {
            let q = &queries[k];
            let val = q[2];
            for i in q[0]..=q[1] {
                let i = i as usize;
                if dp[i][nums[i] as usize] {
                    continue;
                }

                for j in (val..=nums[i]).rev() {
                    dp[i][j as usize] |= dp[i][(j - val) as usize];
                }
                if dp[i][nums[i] as usize] {
                    zeros += 1;
                }
            }
            k += 1;
        }

        if zeros < nums.len() {
            -1
        } else {
            k as i32
        }
    }
}

