class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        const int INF = 1e7;
        vector<int> ans(n, INF);

        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) {
                ans[i] = 0;
                continue;
            }
            vector<bool> dp(nums[i] + 1, false);
            dp[0] = true;
            for (int k = 0; k < q; k++) {
                int l = queries[k][0], r = queries[k][1], v = queries[k][2];
                if (l <= i && i <= r) {
                    for (int j = nums[i]; j >= v; j--) {
                        if (dp[j - v]) {
                            dp[j] = true;
                        }
                    }
                }
                if (dp[nums[i]]) {
                    ans[i] = k + 1;
                    break;
                }
            }
        }

        int maxK = 0;
        for (int i = 0; i < n; i++) {
            if (ans[i] == INF) {
                return -1;
            }
            maxK = max(maxK, ans[i]);
        }
        return maxK;
    }
};

