class Solution:
    def add(self, count, val):
        for i in range(1000-val, -1, -1):
            count[i+val] += count[i]
    def delete(self, count, val):
        for i in range(val, 1001):
            count[i] -= count[i-val]
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        res = -1
        heap = []
        count = [1] + [0] * 1000
        for i in range(len(nums)):
            if not nums[i]: continue
            while heap and heap[0][0] < i:
                self.delete(count, heap[0][1])
                heappop(heap)
            for qid in range(len(queries)):
                if count[nums[i]]: 
                    break
                elif not queries[qid]:
                    continue
                elif queries[qid][0] <= i <= queries[qid][1]:
                    self.add(count, queries[qid][2])
                    heappush(heap, (queries[qid][1], queries[qid][2]))
                    res = max(res, qid)
                    queries[qid].clear()
            if not count[nums[i]]:
                return -1

        return res + 1

