class Solution:
    def isSubsetSum(self, arr, target):
        n = len(arr)
        prev = [False] * (target + 1)
        curr = [False] * (target + 1)

        prev[0] = True 

        for i in range(1, n + 1):
            for j in range(target + 1):
                if j < arr[i - 1]:
                    curr[j] = prev[j]
                else:
                    curr[j] = prev[j] or prev[j - arr[i - 1]]
            prev = curr.copy() 

        return prev[target]

    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        left, right = 0, len(queries) + 1  # left =0
        def can_zero_out(k):
            contributions = defaultdict(list) 

            for i in range(k):
                l, r, val = queries[i]
                for j in range(l, r + 1):
                    contributions[j].append(val)

            for i in range(n):
                if nums[i] == 0:
                    continue  

                target = nums[i]
                available_vals = contributions[i]

                if not available_vals or not self.isSubsetSum(available_vals, target):
                    return False

            return True

        while left < right: 
            mid = (left + right) // 2
            if can_zero_out(mid):
                right = mid  
            else:
                left = mid + 1  

        return left if left <= len(queries) else -1

