#include <bits/stdc++.h>
using namespace std;

class Solution {
private:
    const int ADIPADAI_PARAVU = 1001;

    bool ellaElementsumZeroVa(const vector<int>& arayam) {
        for (int enn : arayam) {
            if (enn != 0) return false;
        }
        return true;
    }

    bool ellaIlakkumSaathiyama(const vector<bitset<1001>>& saathiya_nilai, const vector<int>& arayam) {
        for (int i = 0; i < arayam.size(); i++) {
            if (!saathiya_nilai[i].test(arayam[i])) {
                return false;
            }
        }
        return true;
    }

    void thuvalaiCheyyavum(vector<bitset<1001>>& saathiya_nilai, int idamirunthu, int idamvarai, int maatrumThogai, const vector<int>& arayam) {
        for (int sthaanam = idamirunthu; sthaanam <= idamvarai; sthaanam++) {
            bitset<1001> puriaThogai = saathiya_nilai[sthaanam];
            puriaThogai <<= maatrumThogai;
            saathiya_nilai[sthaanam] |= puriaThogai;
            for (int nilai = arayam[sthaanam] + 1; nilai < ADIPADAI_PARAVU; nilai++) {
                saathiya_nilai[sthaanam].reset(nilai);
            }
        }
    }

public:
    int minZeroArray(vector<int>& arayam, vector<vector<int>>& sodhanaigal) {
        if (ellaElementsumZeroVa(arayam)) {
            return 0;
        }
        int mozhumaiAlavu = arayam.size();
        vector<bitset<1001>> saathiya_nilai(mozhumaiAlavu);
        for (int i = 0; i < mozhumaiAlavu; i++) {
            saathiya_nilai[i].reset();
            saathiya_nilai[i].set(0); 
        }
        for (int sodhanaiEnn = 0; sodhanaiEnn < sodhanaigal.size(); sodhanaiEnn++) {
            const auto& sodhanai = sodhanaigal[sodhanaiEnn];
            int idamirunthu = sodhanai[0] , idamvarai = sodhanai[1] , maatrumThogai = sodhanai[2];
            thuvalaiCheyyavum(saathiya_nilai, idamirunthu, idamvarai, maatrumThogai, arayam);
            if (ellaIlakkumSaathiyama(saathiya_nilai, arayam)) {
                return sodhanaiEnn + 1;
            }
        }
        return -1;
    }
};

