class Solution {
    bool check(vector<int>& a, vector<vector<int>>& q, int k) {
        int n = a.size();
        const int MAX=1000;
        for(int i = 0; i < n; i++) {
            vector<int> dp(MAX+1, 0);
            dp[0] = 1;
            for(int j = 0; j < k; j++) {
                int l=q[j][0], r=q[j][1], val=q[j][2];
                if(l<=i && i<=r) for (int x=MAX; x>=val; x--) dp[x]|=dp[x-val];
            }
            if(!dp[a[i]]) return false;
        }
        return true;
    }
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int low = 0, high = q.size(), ans = -1;
        while (low<=high) {
            int mid = low+(high-low)/2;
            if (check(a, q, mid)) ans=mid, high=mid-1;
            else low=mid+1;
        }
        return ans;
    }
};

