class Solution {
    int findMinQueriesRequired(int idx, int target, vector<vector<int>>& queries) {
        if (target == 0)
            return 0;
        vector<bool> dp(target + 1);
        dp[0] = 1;
        int queryCnt = 0;
        for (auto& query: queries) {
            queryCnt++;
            int l = query[0], r = query[1], val = query[2];
            if (idx < l || idx > r)
                continue;
            for (int j = target; j >= val; --j) {
                dp[j] = dp[j] | dp[j - val];
            }
            if (dp[target])
                return queryCnt;
        }
        return -1;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int maxQuery = 0;

        for (int i=0; i<nums.size(); ++i) {
            int curQuery = findMinQueriesRequired(i, nums[i], queries);
            if (curQuery == -1)
                return -1;
            maxQuery = max(maxQuery, curQuery);
        }
        return maxQuery;
    }
};

