import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int[][] queriesCopy = queries;
        int lo = 0, hi = queries.length, ans = -1;
        while (lo <= hi) {
            int mid = (lo + hi) / 2;
            if (canZero(nums, queriesCopy, mid)) {
                ans = mid;
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }
        return ans;
    }

    private boolean canZero(int[] nums, int[][] queries, int k) {
        int n = nums.length;
        for (int i = 0; i < n; i++) {
            List<Integer> vals = new ArrayList<>();
            for (int j = 0; j < k; j++) {
                if (queries[j][0] <= i && i <= queries[j][1]) {
                    vals.add(queries[j][2]);
                }
            }
            if (!subsetSum(vals, nums[i])) {
                return false;
            }
        }
        return true;
    }

    private boolean subsetSum(List<Integer> vals, int target) {
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;
        for (int val : vals) {
            for (int s = target; s >= val; s--) {
                if (dp[s - val]) dp[s] = true;
            }
        }
        return dp[target];
    }
}

