class Solution {
    int[] nums;
    Map<Integer,Integer> map=new HashMap<>();
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, sum = 0, k = 0;
        this.nums=nums;
        for(int i=0;i<n;i++){
            k=Math.max(k,solve(queries,nums[i],i,0));
            map=new HashMap<>();
        }
        if(k==100000000){
            return -1;
        }
        return k;
    }
    int solve(int[][] queries,int num,int idx,int qi){
        int key=num+1001*idx+ 10011*qi;
        if(num==0){
            return qi;
        }
        if(num<0 || qi==queries.length){
            return 100000000;
        }
        if(map.containsKey(key)){
            return map.get(key);
        }
        int r=100000000;
        if(idx>=queries[qi][0] && idx<=queries[qi][1] && queries[qi][2]<=nums[idx]){
            r=solve(queries,num-queries[qi][2],idx,qi+1);
        }
        r=Math.min(r,solve(queries,num,idx,qi+1));
        map.put(key,r);
        return r;
    }
}

