class Solution {
  int minZeroArray(List<int> nums, List<List<int>> queries) {
  int a = nums.length;
  List<List<bool>> b =
      List.generate(a, (_) => List<bool>.filled(1001, false));
  for (int c = 0; c < a; c++) {
    b[c][0] = true;
  }
  bool d = true;
  for (var e in nums) {
    if (e != 0) {
      d = false;
      break;
    }
  }
  if (d) return 0;
  for (int f = 0; f < queries.length; f++) {
    int g = queries[f][0];
    int h = queries[f][1];
    int i = queries[f][2];
    for (int j = g; j <= h; j++) {
      List<bool> k = List<bool>.from(b[j]);
      k = _s(k, i);
      for (int l = 0; l < 1001; l++) {
        b[j][l] = b[j][l] || k[l];
      }
      for (int m = nums[j] + 1; m < 1001; m++) {
        b[j][m] = false;
      }
    }
    bool n = true;
    for (int o = 0; o < a; o++) {
      if (!b[o][nums[o]]) {
        n = false;
        break;
      }
    }
    if (n) return f + 1;
  }
  return -1;
}

List<bool> _s(List<bool> p, int t) {
  List<bool> r = List<bool>.filled(1001, false);
  for (int s = 0; s < 1001 - t; s++) {
    if (p[s]) r[s + t] = true;
  }
  return r;
}

}

