class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int ans = -1, low = 0, high = queries.length;
        while(low<=high){
            int mid = low + (high-low)/2;
            if(isPossible(nums, queries, mid)){
                ans = mid;
                high = mid-1;
            }else{
                low = mid+1;
            }
        }
        return ans;
    }

    public boolean isPossible(int[] nums, int[][] queries, int k){
        Map<Integer,List<Integer>> map = new HashMap<>();
        int n = nums.length;
        for(int i=0;i<n;i++){
            map.put(i, new ArrayList<>());
        }
        for(int i=0;i<k;i++){
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];

            for(int idx=l;idx<=r;idx++){
                map.get(idx).add(val);
            }
        }
        return check(map, nums);
    }

    public boolean check(Map<Integer,List<Integer>> map, int[] nums){
        for(int i=0;i<nums.length;i++){
            if(nums[i]>0){
                int[][] dp = new int[map.get(i).size()][nums[i]+1];
                for(int[] sub : dp){
                    Arrays.fill(sub, -1);
                }
                if(isSumExist(map.get(i), 0, nums[i], dp)==0){
                    return false;
                }
            }
        }
        return true;
    }

    public int isSumExist(List<Integer> list, int idx, int sum, int[][] dp){
        if(idx==list.size()){
            return sum==0?1:0;
        }
        if(sum<0){
            return 0;
        }

        if(dp[idx][sum]!=-1){
            return dp[idx][sum];
        }
        //take it
        int take = isSumExist(list, idx+1, sum-list.get(idx), dp);
        //not take it
        int notTake = isSumExist(list, idx+1, sum, dp);
        return dp[idx][sum] = (take==1 || notTake==1)?1:0;
    }

}

