#include <vector>
#include <bitset>
using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& elements, vector<vector<int>>& operations) {
        int size = elements.size();
        pair<vector<int>, vector<vector<int>>> dataPair = {elements, operations};

        vector<bitset<1001>> bitMasks = initializeBitMasks(size);

        if (checkIfAllZero(elements)) return 0;

        for (int opIndex = 0; opIndex < operations.size(); opIndex++) {
            applyOperation(bitMasks, elements, operations[opIndex]);

            if (isTransformationComplete(bitMasks, elements)) return opIndex + 1;
        }

        return -1;
    }

private:
    vector<bitset<1001>> initializeBitMasks(int size) {
        vector<bitset<1001>> bitMasks(size);
        for (int i = 0; i < size; i++) {
            bitMasks[i].reset();
            bitMasks[i].set(0, true);
        }
        return bitMasks;
    }

    bool checkIfAllZero(const vector<int>& elements) {
        for (int value : elements) {
            if (value != 0) return false;
        }
        return true;
    }

    void applyOperation(vector<bitset<1001>>& bitMasks, const vector<int>& elements, const vector<int>& operation) {
        int start = operation[0], end = operation[1], shiftValue = operation[2];

        for (int i = start; i <= end; i++) {
            bitMasks[i] |= (bitMasks[i] << shiftValue);
            for (int j = elements[i] + 1; j < 1001; j++) {
                bitMasks[i].reset(j);
            }
        }
    }

    bool isTransformationComplete(const vector<bitset<1001>>& bitMasks, const vector<int>& elements) {
        for (int i = 0; i < elements.size(); i++) {
            if (!bitMasks[i].test(elements[i])) return false;
        }
        return true;
    }
};

