class Solution {
public:

    bool target(vector<int> arr, int sum) {
        int n = arr.size();
        vector<bool> prev(sum + 1, false), curr(sum + 1);

        prev[0] = true;
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= sum; j++) {
                if (j < arr[i - 1])
                    curr[j] = prev[j];
                else
                    curr[j] = (prev[j] || prev[j - arr[i - 1]]);
            }
            prev = curr;
        }
        return prev[sum];
    }

    bool possible(vector<int>& nums, vector<vector<int>>& qr, int mid)
    {

        vector<vector<int>> mat(nums.size());

        for(int i=0; i<mid; i++)
        {
            int l = qr[i][0];
            int r = qr[i][1];
            int val = qr[i][2];

            for(; l<=r; l++)
            {
                mat[l].push_back(val);
            }
        }

        for(int i=0; i<nums.size(); i++)
        {
            if(!target(mat[i], nums[i]))
            {
                return false;
            }
        }

        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& qr) {

        int s = 0;
        int e = qr.size();

        int ans=-1;

        while(s<=e)
        {
            int mid = s + (e-s)/2;

            if(possible(nums, qr, mid))
            {
                ans = mid;
                e = mid-1;
            }
            else
            {
                s = mid+1;
            }
        }

        return ans;

    }
};

