class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> qsum(n);
        for (auto &q:queries) {
            for (int i = 0; i < n; i++) {
            // for (int i = q[0]; i <= q[1]; i++) {
                if (i >= q[0] && i <= q[1]) {
                    // qsum[i][0] += q[2];
                    qsum[i].push_back(q[2]);
                }
                else qsum[i].push_back(0);
            }
        }

        // int idx = 0;
        // for (auto &it:qsum) {
        //     cout << nums[idx++] << ": ";
        //     for (auto &i:it) cout << i << " ";
        //     cout << endl; 
        // }
        // cout << endl; 

        auto subsetSum = [](vector<int> &subset, int k){
            if (k == 0) return 0;
            unordered_set<int> sums = {0};
            for (int i = 0; i < subset.size(); i++) {
                unordered_set<int> nsums = sums;
                for (int s:sums) {
                    if (s + subset[i] == k) {
                        return i + 1;
                    }
                    nsums.insert(s + subset[i]);
                }
                sums = nsums;
            }
            return -1;
        };

        int ans = -1;
        for (int i = 0; i < n; i++) {
            int t = subsetSum(qsum[i], nums[i]);
            if (t == -1)    return -1;
            ans = max(ans, t);
        }

        return ans ;
    }
};

