class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int low = -1;
        int high = queries.length-1;
        if(!isPos(nums,queries,high)) return -1;
        while (low<=high) {
            int mid = high - (high - low)/2;
            if(isPos(nums, queries, mid)){
                high = mid - 1;
            }
            else{
                low = mid + 1;
            }
        }
        return low+1;
    }

    boolean isPos(int nums[],int queries[][],int k){
        int n = nums.length;
        ArrayList<ArrayList<Integer>> arr = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            arr.add(new ArrayList<>());
        }
        for (int i = 0; i <=k; i++) {
            int left = queries[i][0];
            int right = queries[i][1];
            int val = queries[i][2];
            for (int j = left; j <= right; j++) {
                arr.get(j).add(val);
            }
        }
        for (int i = 0; i < n; i++) {
            if(!isSubsetSum(arr.get(i),nums[i])) return false;
        }
        return true;
    }

    boolean isSubsetSum(ArrayList<Integer> arr, int target) {
        if(arr.size()==0){
            if(target==0) return true;
            else return false;
        }
        boolean dp[][] = new boolean[arr.size()][target+1];
        for(int i=0;i<arr.size();i++)
        dp[i][0] = true;
        if(arr.get(0)<=target)
        dp[0][arr.get(0)] = true;

        for(int i=1;i<arr.size();i++){
            for(int t=1;t<=target;t++){
                boolean notTake = dp[i-1][t];
                boolean take = false;
                if(arr.get(i)<=t)
                take = dp[i-1][t-arr.get(i)];
                dp[i][t] = take||notTake;
            }
        }

        return dp[arr.size()-1][target];
    }
}

