class Solution {
public:
    bool ispos(vector<int>& nums, int target) {
        int n = nums.size();
        vector<vector<bool>> dp(n + 1, vector<bool>(target+1, 0));
        dp[n][0] = true;
        for (int i = n - 1; i >= 0; i--) {
            dp[i][0] = true;
            for (int j = 1; j <= target; j++) {
                bool maxi = dp[i + 1][j];
                if (j - nums[i] >= 0) {
                    if (dp[i + 1][j - nums[i]] == 1) {
                        maxi = 1;
                    }
                }
                dp[i][j] = maxi;
            }
        }
        return dp[0][target];
    }
    bool check(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size(), m = queries.size();
        vector<vector<int>> v(n);
        for (int i = 0; i < k; i++) {
            int a = queries[i][0], b = queries[i][1], c = queries[i][2];
            for (int j = a; j <= b; j++) {
                v[j].push_back(c);
            }
        }
        bool is_it = true;
        for (int i = 0; i < n; i++) {
            if (ispos(v[i], nums[i]) == false) {
                return false;
            }
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        if (check(nums, queries, m) == false) {
            return -1;
        }
        int l = 0, h = m - 1;
        int ans = m;
        while (l <= h) {
            int mid = (l + h) / 2;
            if (check(nums, queries, mid) == true) {
                ans = mid;
                h = mid - 1;
            } else {
                l = mid + 1;
            }
        }
        return ans;
    }
};

