class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<bitset<1001>> dp(n);

        FOR(i,0,n){
            dp[i].reset();
            dp[i].set(0, true);
        }

        bool flag = true;
        FOR(i,0,n) {
            if (nums[i] != 0) {
                flag = false;
                break;
            }
        }

        if (flag == true) return 0;

        FOR(k,0,(int)queries.size()) {
            FORR(j , queries[k][0] , queries[k][1]) {
                dp[j] |= (dp[j] << queries[k][2]);
                for (int p = nums[j] + 1; p < 1001; p++) {
                    dp[j].reset(p);
                }
            }

            bool all = true;
            for (int j = 0; j < n; j++) {
                if (!dp[j].test(nums[j])) {
                    all = false;
                    break;
                }
            }

            if (all) return k + 1;
        }

