class Solution {
    public int minZeroArray(int[] arr, int[][] q) {

        int res = 0;
        for(int i = 0; i<arr.length; i++){
            int x = helper(arr[i], i, arr, q);
            if(x == Integer.MAX_VALUE) return -1;
            res = Math.max(res, x);
        }
        return res;
    }

    Integer[][] dp;
    public int helper(int val, int inx, int[] arr, int[][] q){
        dp = new Integer[val+1][q.length];
        return solve(val, arr, q, inx, 0);
    }

    public int solve(int val,int[] arr, int[][] q, int inx1, int inx2){
        if(val == 0) return inx2;
        if(inx2 >= q.length) return Integer.MAX_VALUE;
        if(dp[val][inx2] !=null) return dp[val][inx2];

        int o1 = solve(val, arr, q, inx1, inx2+1);

        int o2 = Integer.MAX_VALUE;

        int l = q[inx2][0];
        int r = q[inx2][1];
        int d = q[inx2][2];

        if(inx1 >= l && inx1 <= r && val>=d){
            o2 = solve(val-d, arr, q, inx1, inx2+1);
        }
        // System.out.println(inx2+" "+ o1+" "+o2);
        return dp[val][inx2] = Math.min(o1,o2);
    }
}

