import java.math.BigInteger;

class Solution {
    public int minZeroArray1F(int[] nums, int[][] queries) {
        int n = nums.length, m = queries.length;
        for (int j = 0; j < m; j ++) {
            int l = queries[j][0], r = queries[j][1], d = queries[j][2], match = 1;
            for (int i = l; i <= r; i ++) {
                nums[i] -= d;
            }
            System.out.println(j + " " + Arrays.toString(nums));
            for (int v : nums) {
                if (v != 0) {
                    match = 0;
                    break;
                }
            }
            if (match == 1) {
                return j + 1;
            }
        }
        return -1;
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, m = queries.length;
        BigInteger[] dp = new BigInteger[n];
        for (int i = 0; i < n; i++) {
            dp[i] = BigInteger.ONE; 
        }

        boolean allZero = true;
        for (int i = 0; i < n; i++) {
            if (!dp[i].testBit(nums[i])) { 
                allZero = false;
                break;
            }
        }
        if (allZero) {
            return 0;
        }

        for (int k = 0; k < m; k++) {
            int l = queries[k][0], r = queries[k][1], v = queries[k][2];
            for (int i = l; i <= r; i++) {
                dp[i] = dp[i].or(dp[i].shiftLeft(v));
            }
            allZero = true;
            for (int i = 0; i < n; i++) {
                if (!dp[i].testBit(nums[i])) {
                    allZero = false;
                    break;
                }
            }
            if (allZero) {
                return k + 1; 
            }
        }
        return -1;
    }
}

