class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        # min and max of search space
        m = len(queries)
        n = len(nums)
        # for each index store the set of all possible numbers it can have
        d = defaultdict(set)

        isZero = set()
        for i, num in enumerate(nums):
            d[i].add(num)
            if num == 0:
                isZero.add(i)
        if len(isZero) == n:
            return 0


        for index, query in enumerate(queries):
            l, r, val = query

            for i in range(l, r+1):
                if i in isZero:
                    continue
                updated = set()
                for num in d[i]:
                    if num-val == 0:
                        updated.add(0)
                        isZero.add(i)
                        break
                    elif num-val > 0:
                        updated.add(num-val)
                d[i].update(updated)

            if len(isZero) == n:
                return index+1
        return -1

