            }
        }
        if (allzero) return 0;
        int k = 0;
        for (int[] query : queries) {
            int l = query[0], r = query[1], val = query[2];
            for (int i = l; i <= r; i++) {
                for (int s = max_sum; s >= val; s--) {
                    if (dp[i][s - val]) {
                        dp[i][s] = true;
                    }
                }
            }
            k++;
            boolean canzero = true;
            for (int i = 0; i < n; i++) {
                if (!dp[i][nums[i]]) {
                    canzero = false;
                    break;
                }
            }
            if (canzero) return k;
        }
        return -1;
    }
}

