# Find min index i so that vals[0..i] can yield a subset sum equal to x
def find_min_ind(x: int, vals: List[int]) -> int:
    n = len(vals)

    if x == 0:
        return -1
    if n == 0:
        return -2

    # dp[i][j] = whether a number i can be sum of a subset of vals[0..j]
    # for i from 0 to x
    dp = [[False] * n for _ in range(x+1)]
    for j in range(n):
        dp[0][j] = True
    for i in range(x+1):
        if vals[0] <= x:
            dp[vals[0]][0] = True
        for j in range(1, n):
            if dp[i][j-1] or (i >= vals[j] and dp[i-vals[j]][j-1]):
                dp[i][j] = True

    for j in range(n):
        if dp[x][j]:
            return j
    return -2

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        k = -1
        for t, d in enumerate(nums):
            vals, indices = [], []
            for i, q in enumerate(queries):
                l, r, val = q[0], q[1], q[2]
                if t >= l and t <= r:
                    vals.append(val)
                    indices.append(i)
            ind = find_min_ind(d, vals)
            #if len(vals) and ind >= 0:
            #    print(d, vals, ind, indices[ind])
            if ind >= 0:
                k = max(k, indices[ind])
            elif ind == -2:
                return -1

        return (k+1)

