class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int l = 0;
        int r = queries.length;
        int ans = -1;
        while (l <= r) {
            int mid = (l + r) / 2;
            if (check(nums, queries, mid)) {
                ans = mid;
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }
        return ans;
    }
    private boolean check(int[] nums, int[][] queries, int time) {
        Map<Integer, List<Integer>> reduce = new HashMap<>();
        Map<Integer, Integer> maxRed = new HashMap<>();
        for (int i = 0; i < time; i++) {
            int li = queries[i][0];
            int ri = queries[i][1];
            int vali = queries[i][2];
            for (int j = li; j <= ri; j++) {
                reduce.computeIfAbsent(j, k -> new ArrayList<>()).add(vali);
                maxRed.put(j, maxRed.getOrDefault(j, 0) + vali);
            }
        }

        for (int i = 0; i < nums.length; i++) {
            if (nums[i] == 0 || nums[i] == maxRed.getOrDefault(i, 0)) {
                continue;
            }
            if (nums[i] > maxRed.getOrDefault(i, 0)) {
                return false;
            }
            int num = nums[i];
            List<Integer> red = reduce.get(i);
            boolean[] dp = new boolean[num + 1];
            dp[0] = true;

            for (int x : red) {
                for (int j = num; j >= x; j--) {
                    dp[j] |= dp[j - x];
                }
            }

            if (!dp[num]) {
                return false;
            }
        }

        return true;
    }
}

