class Solution {
 public:
  int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    if (ranges::max(nums) == 0) {
      return 0;
    }
    int n = nums.size(), m = queries.size(), k = 0, i = 0;
    vector<unordered_set<int>> diffArr(n, unordered_set<int>{0});
    for (; k < m && i < n; ++k) {
      int l = max(queries[k][0], i), r = queries[k][1], val = queries[k][2];
      if (r < i) {
        continue;
      }
      for (; l <= r; ++l) {
        unordered_set<int> toAdd{};
        for (int diff : diffArr[l]) {
          toAdd.insert(diff + val);
        }
        diffArr[l].insert(toAdd.begin(), toAdd.end());
      }
      for (; i < n && diffArr[i].contains(nums[i]); ++i);
    }
    return i == n ? k : -1;
  }
};

