class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int N = nums.length;
        int M = queries.length;

        int sum = 0;
        for (int num : nums) {
            sum += num;
        }
        if (sum == 0) {
            return 0;
        }

        int low = 1, high = M;

        while (low <= high) {
            int mid = (low + high) / 2;
            if (go(mid, nums, queries, N)) {
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }

        int ans = high + 1;
        return ans > M ? -1 : ans;
    }

    private boolean go(int k, int[] nums, int[][] queries, int N) {
        ArrayList<Integer>[] arr = new ArrayList[N];
        for (int i = 0; i < N; i++) {
            arr[i] = new ArrayList<>();
        }

        for (int i = 0; i < k; i++) {
            int li = queries[i][0], ri = queries[i][1], vali = queries[i][2];
            for (int j = li; j <= ri; j++) {
                arr[j].add(vali);
            }
        }


        for (int i = 0; i < N; i++) {
            Map<String, Boolean> memo = new HashMap<>();
            if (!helper(0, nums[i], arr, memo, i)) {
                return false;
            }
        }
        return true;
    }

    private boolean helper(int i, int val, ArrayList<Integer>[] arr, Map<String, Boolean> memo, int idx) {
        if (val == 0) return true;
        if (i == arr[idx].size()) return val == 0;

        String key = i + "-" + val;
        if (memo.containsKey(key)) {
            return memo.get(key);
        }

        boolean x = helper(i + 1, val, arr, memo, idx);
        boolean y = false;
        if (arr[idx].get(i) <= val) {
            y = helper(i + 1, val - arr[idx].get(i), arr, memo, idx);
        }

        boolean result = x || y;
        memo.put(key, result);
        return result;
    }
}

