class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        index_values = {}
        for i in range(len(nums)):
            index_values[i] = set([0])

        if sum(nums) == 0:
            return 0

        for k, query in enumerate(queries):
            l, r, v = query
            for i in range(l, r + 1):
                new_values = set()
                for value in index_values[i]:
                    new_values.add(value + v)
                index_values[i] = new_values.union(index_values[i])

            all_found = True
            for i in range(len(nums)):
                if nums[i] not in index_values[i]:
                    all_found = False
                    break

            if all_found:
                return k + 1

        return -1

