class Solution {
public:
    int fint(int i, int sum, vector<pair<int, int>> &v, vector<vector<int>> &dp) {
        int n = v.size();
        if (i >= n || sum < 0) return INT_MAX;

        if (sum == 0) return i; // ?

        if (dp[i][sum] != -1) return dp[i][sum];

        if (sum - v[i].first == 0) return dp[i][sum] = v[i].second;

        int I = fint(i + 1, sum - v[i].first, v, dp);
        int E = fint(i + 1, sum, v, dp);

        return dp[i][sum] = min(I, E);
    }

    int f(vector<pair<int, int>> &v, int x) {
        int n = v.size();
        vector<vector<int>> dp(n + 1, vector<int> (x + 1, -1));
        int ans = fint(0, x, v, dp);
        return ans == INT_MAX ? -1: ans;
    }

    int minZeroArray(vector<int>& a, vector<vector<int>>& b) {
        int n = a.size();
        vector<vector<pair<int, int>>> ab(n);
        for (int q = 0; q < b.size(); q++) {
            int l = b[q][0], r = b[q][1], x = b[q][2];
            for (int i = l; i <= r; i++) {
                ab[i].push_back({x, q + 1});
            }
        }

        int k = 0;
        for (int i = 0; i < n; i++) {
            if (a[i] == 0) continue;
            int required = f(ab[i], a[i]);

            if (required == -1) {
                return -1;
            }

            k = max(k, required);
        }

        return k;
    }
};

