class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int[][] dp = new int[n][1001];
        for (int[] d : dp) {
            Arrays.fill(d, -1);
            d[0] = 0;
        }

        boolean flag = true;
        for (int j = 0; j < nums.length; ++j) {
            if (dp[j][nums[j]] == -1) {
                flag = false;
                break;
            }
        }
        if (flag) return 0;


        for (int i = 0; i < queries.length; ++i) {
            int start = queries[i][0], end = queries[i][1], val = queries[i][2];
            for (int j = start; j <= end; ++j) {
                for (int k = nums[j]; k >= 0; --k) {
                    if (dp[j][k] == 0 && k + val <= nums[j]) {
                        dp[j][k + val] = 0;
                    }
                }
            }
            flag = true;
            for (int j = 0; j < nums.length; ++j) {
                if (dp[j][nums[j]] == -1) {
                    flag = false;
                    break;
                }
            }
            if (flag) return i + 1;        
        }
        return -1;
    }
}

