class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        dp = [[False] * 1001 for _ in range(n)]

        for j in range(n):
            dp[j][0] = True

        if all(num == 0 for num in nums):
            return 0

        for k, (l, r, v) in enumerate(queries):
            for j in range(l, r + 1):
                for pos in range(1000, v - 1, -1):
                    dp[j][pos] |= dp[j][pos - v]
                for pos in range(nums[j] + 1, 1001):
                    dp[j][pos] = False

            if all(dp[j][nums[j]] for j in range(n)):
                return k + 1

        return -1

